import React, { useState, useEffect } from "react";
import { Container, Row, Col, Table, Button, Modal } from "react-bootstrap";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from "react-router-dom";

function Mahasiswa() {
    const [mhs, setMhs] = useState([]);
    const [jrs, setJrsn] = useState([]);
    const [show, setShow] = useState(false);
    const [updateId, setUpdateId] = useState(null);
    const [nama, setNama] = useState("");
    const [nrp, setNrp] = useState("");
    const [id_jurusan, setIdJurusan] = useState("");
    const [gambar, setGambar] = useState(null);
    const [swa_foto, setSwaFoto] = useState(null);
    const [validation, setValidation] = useState({});
    const navigate = useNavigate();

    const url = "http://localhost:3000/static/";

    useEffect(() => {
        fectData();
    }, []);

    const fectData = async () => {
        try {
            const response1 = await axios.get("http://localhost:3000/mahasiswa");
            const data1 = await response1.data.data;
            setMhs(data1);

            const response2 = await axios.get("http://localhost:3000/jurusan");
            const data2 = await response2.data.data;
            setJrsn(data2);
        } catch (error) {
            console.error("Kesalahan: ", error);
        }
    };

    const handleShow = () => setShow(true);

    const handleClose = () => {
        console.log("Modal is closing");
        setShow(false);
    };

    const handleNamaChange = (e) => {
        setNama(e.target.value);
    };

    const handleNrpChange = (e) => {
        setNrp(e.target.value);
    };

    const handleIdJurusanChange = (e) => {
        setIdJurusan(e.target.value);
    };

    const handleGambarChange = (e) => {
        const file = e.target.files[0];
        setGambar(file);
    };

    const handleSwaFotoChange = (e) => {
        const file = e.target.files[0];
        setSwaFoto(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();

        formData.append("nama", nama);
        formData.append("nrp", nrp);
        formData.append("id_jurusan", id_jurusan);
        formData.append("gambar", gambar);
        formData.append("swa_foto", swa_foto);

        try {
            await axios.post("http://localhost:3000/upload", formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });
            navigate("/mhs");
            fectData();
        } catch (error) {
            console.error("Kesalahan: ", error);
            setValidation(error.response.data);
        }
    };

    const handleUpdate = (id) => {
        setUpdateId(id);
        setShow(true);
    };

    const handleUpdateSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();

        formData.append("nama", nama);
        formData.append("nrp", nrp);
        formData.append("id_jurusan", id_jurusan);
        formData.append("gambar", gambar);
        formData.append("swa_foto", swa_foto);

        try {
            await axios.put(`http://localhost:3000/update/${updateId}`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });
            setShow(false);
            fectData();
        } catch (error) {
            console.error("Kesalahan: ", error);
            setValidation(error.response.data);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3000/delete/${id}`);
            fectData();
        } catch (error) {
            console.error("Kesalahan: ", error);
        }
    };

    return (
        <Container>
            <Row>
                <Col>
                    <h2>Data Mahasiswa</h2>
                    <Button variant="primary" onClick={handleShow}>
                        Tambah
                    </Button>
                </Col>
            </Row>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">No</th>
                        <th scope="col">Nama</th>
                        <th scope="col">Jurusan</th>
                        <th scope="col">gambar</th>
                        <th scope="col">swa_foto</th>
                        <th scope="col">Actions</th>
                    </tr>
                </thead>

                <tbody>
                    {mhs.map((mh, index) => (
                        <tr key={mh.id_m}>
                            <td>{index + 1}</td>
                            <td>{mh.nama}</td>
                            <td>{mh.jurusan}</td>
                            <td>
                                <img src={url + mh.gambar} height="100" alt="gambar" />
                            </td>
                            <td>
                                <img src={url + mh.swa_foto} height="100" alt="swa_foto" />
                            </td>
                            <td>
                                <Button variant="info" onClick={() => handleUpdate(mh.id_m)}>Update</Button>{" "}
                                <Button variant="danger" onClick={() => handleDelete(mh.id_m)}>Delete</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <Row>
                <Table striped bordered hover>
                    {/* Tabel Mahasiswa */}
                </Table>
            </Row>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Tambah Data</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={updateId ? handleUpdateSubmit : handleSubmit}>
                        <div className="mb-3">
                            <label className="form-label">Nama:</label>
                            <input
                                type="text"
                                className="form-control"
                                value={nama}
                                onChange={handleNamaChange}
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">NRP:</label>
                            <input
                                type="text"
                                className="form-control"
                                value={nrp}
                                onChange={handleNrpChange}
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Jurusan:</label>
                            <select
                                className="form-select"
                                value={id_jurusan}
                                onChange={handleIdJurusanChange}
                            >
                                {jrs.map((jr) => (
                                    <option key={jr.id_j} value={jr.id_j}>
                                        {jr.nama_jurusan}
                                    </option>
                                ))}
                            </select>
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Gambar:</label>
                            <input
                                type="file"
                                className="form-control"
                                accept="image/*"
                                onChange={handleGambarChange}
                            />
                        </div>
                        <div className="mb-3">
                            <label className="form-label">Swa Foto:</label>
                            <input
                                type="file"
                                className="form-control"
                                accept="image/*"
                                onChange={handleSwaFotoChange}
                            />
                        </div>
                        <Button variant="secondary" onClick={handleClose}>
                            Cancel
                        </Button>
                        <Button type="submit" variant="primary">
                            {updateId ? "Update" : "Submit"}
                        </Button>
                    </form>
                </Modal.Body>
            </Modal>
        </Container>
    );
}

export default Mahasiswa;
