import React, { useState, useEffect } from "react";
import { Container, Row, Col, Table, Button, Modal } from "react-bootstrap";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from "react-router-dom";

function Mahasiswa() {
  const [mhs, setMhs] = useState([]);
  const [jrs, setJrsn] = useState([]);
  const [show, setShow] = useState(false);
  const [nama, setNama] = useState("");
  const [nrp, setNrp] = useState("");
  const [id_jurusan, setIdJurusan] = useState("");
  const [gambar, setGambar] = useState(null);
  const [swa_foto, setSwaFoto] = useState(null);
  const [validation, setValidation] = useState({});
  const [isEditing, setIsEditing] = useState(false);
  const [selectedMhs, setSelectedMhs] = useState(null);
  const navigate = useNavigate();

  const url = "http://localhost:3000/static/";

  useEffect(() => {
    fectData();
  }, []);

  const fectData = async () => {
    try {
      const response1 = await axios.get("http://localhost:3000/mahasiswa");
      const data1 = await response1.data.data;
      setMhs(data1);

      const response2 = await axios.get("http://localhost:3000/");
      const data2 = await response2.data.data;
      setJrsn(data2);
    } catch (error) {
      console.error("Kesalahan: ", error);
    }
  };

  const handleShow = () => {
    setShow(true);
    setIsEditing(false);
    setSelectedMhs(null);
    setNama("");
    setNrp("");
    setIdJurusan("");
    setGambar(null);
    setSwaFoto(null);
  };

  const handleClose = () => {
    setShow(false);
  };

  const handleEdit = (mh) => {
    setShow(true);
    setIsEditing(true);
    setSelectedMhs(mh);
    setNama(mh.nama);
    setNrp(mh.nrp);
    setIdJurusan(mh.id_jurusan);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:3000/mahasiswa/delete/${id}`);
      fectData();
    } catch (error) {
      console.error("Kesalahan: ", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("nama", nama);
    formData.append("nrp", nrp);
    formData.append("id_jurusan", id_jurusan);
    formData.append("gambar", gambar);
    formData.append("swa_foto", swa_foto);

    try {
      if (isEditing) {
        await axios.put(`http://localhost:3000/mahasiswa/update/${selectedMhs.id}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
      } else {
        await axios.post("http://localhost:3000/upload", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        });
      }
      navigate("/mhs");
      fectData();
    } catch (error) {
      console.error("Kesalahan: ", error);
      setValidation(error.response.data);
    }
  };

  return (
    <Container>
      <Row>
        <Col>
          <h2>Data Mahasiswa</h2>
          <Button variant="primary" onClick={handleShow}>
            Tambah
          </Button>
        </Col>
      </Row>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th scope="col">No</th>
            <th scope="col">Nama</th>
            <th scope="col">Jurusan</th>
            <th scope="col">gambar</th>
            <th scope="col">swa_foto</th>
            <th scope="col">Aksi</th>
          </tr>
        </thead>
        <tbody>
          {mhs.map((mh, index) => (
            <tr key={mh.id}>
              <td>{index + 1}</td>
              <td>{mh.nama}</td>
              <td>{mh.jurusan}</td>
              <td>
                <img src={url + mh.gambar} alt="gambar" height="100" />
              </td>
              <td>
                <img src={url + mh.swa_foto} alt="swa_foto" height="100" />
              </td>
              <td>
                <Button variant="info" onClick={() => handleEdit(mh)}>
                  Edit
                </Button>
                <Button variant="danger" onClick={() => handleDelete(mh.id)}>
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{isEditing ? "Edit Data" : "Tambah Data"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            {/* ... (Form inputs and submit button) */}
          </form>
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Mahasiswa;
