import React, { useState, useEffect } from "react";
import { Container, Row, Col, Table, Button, Modal } from "react-bootstrap";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { useNavigate } from "react-router-dom";

function Mahasiswa() {
  const [mhs, setMhs] = useState([]);
  const [jrs, setJrsn] = useState([]);
  const [show, setShow] = useState(false);
  const [updateShow, setUpdateShow] = useState(false);
  const [nama, setNama] = useState("");
  const [nrp, setNrp] = useState("");
  const [id_jurusan, setIdJurusan] = useState("");
  const [gambar, setGambar] = useState(null);
  const [swa_foto, setSwaFoto] = useState(null);
  const [validation, setValidation] = useState({});
  const [updateData, setUpdateData] = useState({
    id: null,
    nama: "",
    nrp: "",
    id_jurusan: "",
    gambar: null,
    swa_foto: null,
  });
  const navigate = useNavigate();

  const url = "http://localhost:3000/static/";

  useEffect(() => {
    fectData();
  }, []);

  const fectData = async () => {
    try {
      const response1 = await axios.get("http://localhost:3000/mahasiswa");
      const data1 = await response1.data.data;
      setMhs(data1);

      const response2 = await axios.get("http://localhost:3000/");
      const data2 = await response2.data.data;
      setJrsn(data2);
    } catch (error) {
      console.error("Kesalahan: ", error);
    }
  };

  const handleShow = () => setShow(true);

  const handleClose = () => {
    console.log("Modal is closing");
    setShow(false);
  };

  const handleUpdateShow = (mahasiswa) => {
    setUpdateData(mahasiswa);
    setUpdateShow(true);
  };

  const handleUpdateClose = () => {
    setUpdateShow(false);
  };

  const handleNamaChange = (e) => {
    setNama(e.target.value);
  };

  const handleNrpChange = (e) => {
    setNrp(e.target.value);
  };

  const handleIdJurusanChange = (e) => {
    setIdJurusan(e.target.value);
  };

  const handleGambarChange = (e) => {
    const file = e.target.files[0];
    setGambar(file);
  };

  const handleSwaFotoChange = (e) => {
    const file = e.target.files[0];
    setSwaFoto(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("nama", nama);
    formData.append("nrp", nrp);
    formData.append("id_jurusan", id_jurusan);
    formData.append("gambar", gambar);
    formData.append("swa_foto", swa_foto);

    try {
      await axios.post("http://localhost:3000/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      navigate("/mhs");
      fectData();
    } catch (error) {
      console.error("Kesalahan: ", error);
      setValidation(error.response.data);
    }
  };

  const handleUpdateSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();

    formData.append("nama", updateData.nama);
    formData.append("nrp", updateData.nrp);
    formData.append("id_jurusan", updateData.id_jurusan);
    formData.append("gambar", updateData.gambar);
    formData.append("swa_foto", updateData.swa_foto);

    try {
      await axios.put(`http://localhost:3000/mahasiswa/${updateData.id}`, formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      handleUpdateClose();
      fectData();
    } catch (error) {
      console.error("Kesalahan: ", error);
      setValidation(error.response.data);
    }
  };

  return (
    <Container>
      <Row>
        <Col>
          <h2>Data Mahasiswa</h2>
          <Button variant="primary" onClick={handleShow}>
            Tambah
          </Button>
        </Col>
      </Row>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">No</th>
            <th scope="col">Nama</th>
            <th scope="col">Jurusan</th>
            <th scope="col">gambar</th>
            <th scope="col">swa_foto</th>
          </tr>
        </thead>
        <tbody>
          {mhs.map((mh, index) => (
            <tr key={mh.id}>
              <td>{index + 1}</td>
              <td>{mh.nama}</td>
              <td>{mh.jurusan}</td>
              <td>
                <img src={url + mh.gambar} height="100" alt={mh.nama} />
              </td>
              <td>
                <img src={url + mh.swa_foto} height="100" alt={mh.nama} />
              </td>
              <td>
                <button onClick={() => handleUpdateShow(mh)} className="btn btn-warning">
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Row>
        <Table striped bordered hover>
          {/* Tabel Mahasiswa */}
        </Table>
      </Row>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Tambah Data</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleSubmit}>
            {/* Add form elements for adding data */}
          </form>
        </Modal.Body>
      </Modal>
      <Modal show={updateShow} onHide={handleUpdateClose}>
        <Modal.Header closeButton>
          <Modal.Title>Update Data</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form onSubmit={handleUpdateSubmit}>
            {/* Add form elements for updating data */}
          </form>
        </Modal.Body>
      </Modal>
    </Container>
  );
}

export default Mahasiswa;
